(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["syarat_ketentuan"],{2065:function(a,n,e){"use strict";e.r(n);var i=function(){var a=this,n=a.$createElement,e=a._self._c||n;return e("div",[e("v-app-bar",{attrs:{app:"",color:"teal",dark:""}},[e("v-btn",{attrs:{icon:""},on:{click:function(n){return n.stopPropagation(),a.$router.go(-1)}}},[e("v-icon",[a._v("mdi-arrow-left-circle")])],1)],1),e("v-card",{staticClass:"d-inline-block mx-auto",attrs:{align:"center"}},[e("v-card",{attrs:{color:"teal darken-4"}},[e("v-img",{attrs:{src:"img/banner web syarat.png",width:"600",height:"150",contain:""}})],1),e("v-card",{attrs:{color:"teal lighten-4"}},[e("v-container",{attrs:{fluid:""}},[e("div",{staticClass:"row mb-5 layout justify-center"},[e("div",{staticClass:"col-md-10 text-justify white-gradient"},[e("p",[a._v("Selamat datang di SiMotor.")]),e("p",[a._v(" Syarat dan Ketentuan ini adalah perjanjian antara pengguna (“Anda”) dan PT Langeng Mitra Utama (“Kami”), sebuah perseroan terbatas yang didirikan dan beroperasi secara sah berdasarkan hukum negara Republik Indonesia dan berdomisili di DKI Jakarta, Indonesia. ")]),e("p",[a._v(" Syarat dan Ketentuan ini mengatur akses dan penggunaan Anda atas aplikasi, situs web (SiMotor.id, SiMotor.co.id, SiMotorku.com dan carimotor.online, dan situs web lain yang kami kelola), konten dan produk yang disediakan oleh kami (selanjutnya, secara bersama-sama disebut sebagai “Aplikasi SiMotor”) dan penggunaan layanan yang tersedia pada Aplikasi SiMotor (“Layanan”). ")]),e("p",[a._v(" Dengan menyetujui Syarat dan Ketentuan ini Anda juga menyetujui Kebijakan Privasi termasuk Ketentuan Penggunaan pada setiap Layanan, dan perubahannya yang merupakan bagian yang tidak terpisahkan dari Syarat dan Ketentuan ini. Dengan mengakses atau menggunakan situs SiMotor, informasi, atau aplikasi lainnya dalam bentuk aplikasi mobile yang disediakan oleh atau dalam Situs, berarti Anda telah memahami dan menyetujui serta terikat dan tunduk dengan segala Syarat dan Ketentuan yang berlaku di situs ini. Apabila Anda tidak setuju dengan Syarat dan Ketentuan ini, kami persilakan Anda untuk tidak melanjutkan penggunaan Layanan. ")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("1. Ketentuan Umum")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Aplikasi dan Layanan ini disediakan kepada Anda dengan tunduk kepada Syarat dan Ketentuan Umum ini. Penggunaan istilah “Anda” atau “pengguna” berarti setiap orang yang menggunakan atau mengakses layanan di dalam aplikasi mobile atau situs baik secara manual maupun melalui sistem otomatis, termasuk setiap orang yang melihat‐lihat layanan dan materi di dalamnya, memberikan komentar terhadap materi atau merespon terhadap iklan yang dipasang di dalam Aplikasi, Situs atau Layanan SiMotor.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("2. Deskripsi Layanan")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("SiMotor mempunyai bisnis utama menjalankan (“Layanan”) aplikasi dan situs iklan baris online (classifieds ads) untuk barang sepeda motor dan bertindak sebagai penyedia jasa penyelenggara dengan sistem user generated content dimana para pengguna aplikasi kami bisa membuat, mengunggah dan memodifikasi konten yang ingin diiklankan. Tujuan aplikasi ini adalah menyediakan berbagai informasi dan layanan yang berkaitan dengan sepeda motor dan juga mempertemukan antara pembeli dan penjual secara online di sebuah aplikasi mobile bernama SiMotor, termasuk situs‐situs dengan domain SiMotor.id, SiMotor.co.id, SiMotorku.com dan carimotor.online. Setiap rujukan terhadap SiMotor dalam Syarat & Ketentuan Umum ini, apabila relevan, adalah termasuk afiliasinya (perusahaan berdasarkan kendali dan kepemilikan bersama), sebagian ataupun seluruh karyawannya, termasuk para direksi dan komisaris.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("3. Batasan Tanggung Jawab")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.1 SiMotor tidak bertanggung jawab atas kebenaran informasi, gambar dan keterangan, termasuk namun tidak terbatas pada perincian mengenai judul iklan, deskripsi, harga, alamat, nomor telepon yang diberikan oleh pemasang iklan. Anda disarankan untuk berhubungan langsung dengan pemasang iklan untuk memastikan informasi yang dicari.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.2 Setiap informasi yang dibuat oleh pemasang iklan di aplikasi SiMotor mengenai perusahaan, perorangan atau badan lainnya, dan/atau mengenai produk atau layanan mereka, bukan merupakan bentuk dukungan atau sokongan, ataupun mengimplikasikan dukungan atau sokongan, terhadap kualitas atau kelayakan dari perorangan, perusahaan atau badan tersebut, atau terhadap layanan atau produknya. Tanggung jawab isi dan/atau materi iklan yang dipasang oleh pemasang iklan (“Materi”) merupakan tanggung jawab sepenuhnya dari pemasang iklan.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.3 SiMotor tidak memiliki hak milik atas iklan yang dipasang oleh pengguna, maupun terlibat dalam proses kesepakatan, pembayaran, pengiriman serta proses purna jual di antara penjual dan pembeli. Perikatan untuk penyerahan barang atau jasa yang terjadi melalui Layanan atau sebagai hasil dari kunjungan dan keberhasilan penawaran yang diajukan oleh pengguna dibuat dengan bebas antara penjual dan pembeli.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.4 Informasi, gambar dan keterangan lainnya yang terdapat atau diterbitkan dalam aplikasi SiMotor juga dapat mengandung ketidakakuratan atau kesalahan tipografis. Para pemasang iklan mungkin akan melakukan perubahan atau perbaikan, dan/atau memperbarui informasi yang tertera di dalam Situs dari waktu ke waktu. SiMotor tidak menanggung kewajiban untuk memperbaharui Materi yang telah menjadi kedaluwarsa atau tidak akurat.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.5 SiMotor tidak bertanggung jawab atas semua jaminan dan kondisi, termasuk segala implikasi dari jaminan, kualitas, kelayakan atas informasi yang disampaikan pemasang iklan. Dalam keadaan apapun, SiMotor tidak bertanggung jawab atas kerugian, baik khusus, langsung, tidak langsung ataupun yang bersifat konsekuensial, maupun kerugian atau kerusakan apapun sebagai akibat dari kerugian atas pemakaian, data atau keuntungan, baik dalam tindakan perikatan, kelalaian atau tindakan kesalahan lainnya, yang muncul dari atau berkaitan dengan penggunaan atau kinerja dari informasi dan/atau gambar yang disediakan oleh pemasang iklan.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.6 SiMotor tidak bertanggung jawab atas akibat langsung atau tidak langsung dari keputusan pengguna/calon pembeli dalam mengajukan penawaran atau tidak mengajukan penawaran kepada pemasang iklan, melakukan jual beli atau tidak melakukan jual beli dengan pemasang iklan.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.7 SiMotor tidak bertanggung jawab atau berkewajiban atas penyerahan barang atau jasa, termasuk kepatuhan pembeli dan pemasang iklan dengan ketentuan perundang‐undangan yang berlaku di Republik Indonesia baik dalam hal penawaran ataupun penjualan barang dan jasa ataupun transaksi lainnya.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.8 SiMotor tidak bertanggung jawab dan tidak berkewajiban terhadap Layanan yang tidak tersedia untuk sementara waktu sehubungan dengan masalah teknis yang berada di luar kendali kami. Melalui Layanan, Anda dapat terhubung ke situs‐situs web lain yang tidak berada di bawah kontrol kami. Penyertaan suatu tautan (hyperlink) bukan berarti merupakan rekomendasi atau dukungan atas pandangan yang ditegaskan di dalam tautan (hyperlink) tersebut. Setiap upaya dilakukan untuk menjaga agar Layanan tetap berjalan lancar.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v('3.9 Layanan yang diberikan SiMotor adalah “sebagaimana adanya”, "dengan segala kekurangan", dan "sebagaimana tersedia" tanpa jaminan apapun juga. SiMotor dengan tegas menolak setiap jaminan mengenai keamanan, keandalan, dan pelaksanaan kegiatan‐kegiatan yang dilakukan oleh para pengguna di Layanan dan jaminan atau ketentuan mengenai tidak ada pelanggaran, dapat diperdagangkan dan kelayakan untuk suatu tujuan tertentu. Dalam hal apapun SiMotor tidak berkewajiban atas suatu kerugian langsung, khusus, tidak langsung, yang merupakan contoh atau yang dapat dihukum, baik dalam suatu perikatan, perbuatan melawan hukum atau teori hukum lain, sekalipun kami telah diberitahu tentang kemungkinan kerugian tersebut.')]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.10 Pengguna memahami dan menyetujui bahwa penggunaan dan pelaksanaan kegiatan sehubungan dengan Situs dan Layanan oleh pengguna adalah atas kebijakan dan risiko pengguna sendiri dan bahwa pengguna sendiri bertanggung jawab penuh atas materi pengguna,dan/atau atas kerusakan/kehilangan atas sistem elektronik Anda dan/atau atas kerusakan/kehilangan atas informasi elektronik yang mungkin saja diakibatkan oleh pelaksanaan kegiatan tersebut.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3.11 Pengguna secara khusus mengakui bahwa SiMotor tidak akan bertanggung jawab atas Materi atau tindakan pencemaran nama baik, tindakan yang melanggar, atau tindakan yang melawan hukum apapun dari pihak ketiga manapun yang berhubungan dengan SiMotor dan Layanan ini. Segala resiko atas kerugian atau kerusakan dari hal‐hal tersebut adalah seluruhnya tanggung jawab pengguna.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("4. Kewajiban Pengguna")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v(" Pengguna wajib menjaga data pribadi miliknya dari kerusakan/kehilangan, termasuk tapi tidak terbatas pada upaya mencegah agar data pribadi miliknya tidak disalahgunakan oleh pihak lain. Pengguna dengan ini mengetahui dan menyetujui bahwa SiMotor tidak dapat dimintai pertanggungjawaban oleh pengguna dan/atau pihak manapun atas hal-hal yang terjadi, termasuk tapi tidak terbatas pada: ")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.1 Kehilangan data pribadi;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.2 Pemalsuan data pribadi pengguna;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.3 Kehilangan penghasilan atau pendapatan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.4 Kehilangan bisnis;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.5 Kehilangan laba atau suatu perikatan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.6 Kehilangan penghematan yang telah diantisipasi sebelumnya;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.7 Kehilangan data;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.8 Kehilangan itikad baik;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.9 Waktu manajemen atau kantor yang tersia-siakan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.10 Kehilangan atau kerugian lain apapun jenisnya, terlepas dari bagaimana hal itu timbul dan apakah disebabkan oleh kelalaian, pelanggaran terhadap suatu perikatan atau lainnya, meskipun dapat diperkirakan sebelumnya;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("Segala kerugian sebagai akibat dari penggunaan data pribadi atau akun milik pengguna yang terindikasi melanggar hukum dan/atau penyalahgunaan oleh pihak yang tidak berwenang; dan")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.11 Segala kerugian dan atau kerusakan karena tidak beroperasinya dan/atau terdapat gangguan terhadap layanan SiMotor akibat terjadinya force majeure, yang termasuk tapi tidak terbatas pada:")]),e("p",{staticClass:"ml-4 ml-lg-4"},[a._v("4.11.1 Bencana alam;")]),e("p",{staticClass:"ml-4 ml-lg-4"},[a._v("4.11.2 Peristiwa kebakaran;")]),e("p",{staticClass:"ml-4 ml-lg-4"},[a._v("4.11.3 Pemogokan;")]),e("p",{staticClass:"ml-4 ml-lg-4"},[a._v("4.11.4 Perang;")]),e("p",{staticClass:"ml-4 ml-lg-4"},[a._v("4.11.5 Huru-hara;")]),e("p",{staticClass:"ml-4 ml-lg-4"},[a._v("4.11.6 Pemberontakan atau tindakan militer lainnya;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.12 Tindakan pihak yang berwenang yang mempengaruhi kelangsungan penyelenggaraan Layanan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.13 Tindakan pihak ketiga yang menyebabkan SiMotor tidak dapat menyediakan Layanan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.14 Segala kerugian sebagai akibat dari penggunaan data pribadi atau akun milik pengguna yang terindikasi melanggar hukum dan/atau penyalahgunaan oleh pihak yang tidak berwenang; dan")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4.15 Adanya keputusan atau perubahan keputusan dari instansi terkait termasuk pemerintah yang berdampak pada pelaksanaan Layanan ini.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Pengguna pada saat memasang iklan wajib memastikan bahwa setiap produk dan/atau jasa yang diiklankan serta Materi iklan telah: (i) memperoleh persetujuan dan/atau perizinan yang dipersyaratkan, (ii) persetujuan dan/atau perizinan yang dipersyaratkan tersebut masih berlaku dan (iii) produk dan/atau jasa yang diiklankan bukan merupakan produk dan/atau jasa yang dilarang untuk diperjualbelikan secara umum berdasarkan ketentuan peraturan perundang- undangan.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Dengan mengakses Layanan, pengguna menerima tanpa batasan atau kualifikasi, dan setuju untuk menggunakan Situs dan Layanan hanya untuk tujuan yang sah dan sesuai dengan syarat dan ketentuan ini, dan pengguna setuju untuk tidak:")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("1. Menggunakan Layanan dengan cara apapun yang akan melumpuhkan, membebani, atau merusak, atau mengganggu penggunaan lainnya dari Layanan, termasuk, namun tidak terbatas pada, kemampuan setiap pengguna untuk terlibat dalam kegiatan real-time melalui Layanan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("2. Menggunakan robot, spider atau perangkat otomatis lainnya, proses atau sarana untuk mengakses Layanan; untuk tujuan apapun, termasuk untuk memantau atau menyalin setiap bahan yang ada di Layanan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("3. Menggunakan proses manual untuk memonitor atau menyalin setiap materi pada Layanan untuk terlibat dalam tujuan tidak sah lainnya tanpa persetujuan tertulis sebelumnya dari kami;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("4. Menggunakan perangkat apapun, perangkat lunak atau routine yang dapat mengganggu kinerja Layanan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("5. Mengganggu, turut campur, menghalangi, merintangi kerja Layanan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("6. Menyalin, mereproduksi, mengubah, memodifikasi, menciptakan karya turunan, atau menampilkan setiap konten secara publik dari Layanan atau Layanan tanpa izin tertulis dari SiMotor; atau melakukan reverse engineering atau mencoba untuk menemukan kode sumber yang berkaitan dengan Layanan atau alat di dalamnya, kecuali sejauh kegiatan tersebut secara tegas diizinkan oleh hukum yang berlaku walaupun ada pembatasan terkait hal ini; atau")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("7. Mencoba untuk mengakses setiap area Layanan atau Situs dimana Anda tidak mempunyai kewenangan atas akses tersebut;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("8. Melakukan upaya apapun yang sekiranya dapat menimbulkan kerugian baik materiil atau immateriil bagi kami ataupun pengguna lain dari SiMotor;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("9. Membuat akun baru dan/atau membeli dan/atau menjual akun untuk menghindari pembatasan yang mungkin kami kenakan pada pengguna kami;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("10. Melakukan pelaporan palsu terhadap pengguna lainnya. Karena setiap laporan pelanggaran kebijakan layanan kami haruslah benar dan disampaikan dengan itikad baik dan dengan maksud mengatasi pelanggaran yang diketahui atau diduga melanggar kebijakan kami;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("11. Melanggar atau menghindari hukum, hak pihak ketiga atau sistem kami, kebijakan, atau penentuan status akun Anda;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("12. Memanipulasi harga barang, termasuk dan tidak terbatas pada penyembunyian harga;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("13. Melakukan pemasangan iklan palsu, tidak akurat, menyesatkan, memfitnah, atau konten mencemarkan nama baik;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("14. Memasang iklan duplikasi/ Double post;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("15. Memindahtangankan ID termasuk namun tidak terbatas pada username dan password Anda ke pihak lain tanpa persetujuan kami;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("16. Mendistribusikan atau mengirim spam, komunikasi elektronik yang tidak diminta atau massal, surat berantai, atau skema piramida;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("17. Mendistribusikan virus atau teknologi lainnya yang dapat membahayakan kami, atau kepentingan atau properti dari pengguna kami;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("18. Mengkomersialkan aplikasi kami atau informasi atau perangkat lunak yang terkait dengan aplikasi tersebut;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("19. Mengumpulkan informasi tentang pengguna kami tanpa persetujuan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("20. Menghindari langkah-langkah teknis yang kami gunakan untuk menyediakan Layanan;")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("21. Memasang iklan menggunakan tautan (hyperlink) dalam bentuk apapun.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Setiap upaya untuk memanipulasi layanan kami atau kebijakan kami dapat mengakibatkan tindakan terhadap akun Anda. Selain penghapusan, Anda mungkin akan dikenakan berbagai tindakan lainnya, termasuk hak memasang iklan atau penangguhan akun Anda, bahkan termasuk dan tidak terbatas pada tindakan hukum.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Karena penggunaan Layanan memerlukan sambungan Internet, Anda mungkin harus membayar biaya yang terkait dengan akses Internet saat menggunakan Layanan atau Situs. Pengguna bertanggung jawab melaksanakan semua pengaturan yang diperlukan guna mengakses Layanan atau Situs.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Pengguna bertanggung jawab memastikan bahwa semua orang yang mengakses Layanan atau Situs melalui hubungan internet milik pengguna, mengetahui Syarat & Ketentuan Umum ini dan mematuhinya.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("5. Ganti Rugi")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Pengguna setuju untuk melepaskan, membela dan membebaskan SiMotor, perusahaan terafiliasinya dan para direkturnya, komisarisnya, pejabatnya, karyawannya dan agennya dari dan terhadap suatu dan semua tuntutan, kewajiban, kerugian dan biaya (termasuk biaya hukum, penggantian kerusakan dan jumlah penyelesaian yang wajar) yang timbul dari atau berkaitan dengan atau yang mungkin timbul dari penggunaan dan/atau pengaksesan Layanan; pelanggaran atas suatu syarat dalam Syarat & Ketentuan Umum ini, Kebijakan Privasi dan Larangan Iklan oleh pengguna; pelanggaran terhadap suatu hak pihak ketiga manapun, termasuk namun tidak terbatas pada hak atas kekayaan intelektual, aset baik yang berupa harta bergerak ataupun harta tidak bergerak, hak privasi; dan/atau tuntutan apapun sehubungan dengan Materi pengguna yang menyebabkan kerugian terhadap pihak ketiga. Dalam hal ini SiMotor juga dapat turut serta dalam suatu pembelaan bersama dengan penasihat hukum pilihannya.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("6. Keterangan Isi/Materi Layanan")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Materi yang ditayangkan di Layanan disediakan tanpa ketentuan atau jaminan apapun dalam hal isi, materi dan ketepatannya. Sejauh diizinkan oleh hukum yang berlaku di Republik Indonesia, SiMotor dengan ini secara tegas mengecualikan:")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("6.1 Semua ketentuan, jaminan dan syarat lain yang dapat dinyatakan secara langsung maupun tidak langsung oleh undang-undang sebagai isi, materi dan ketepatannya; dan")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("6.2 Kewajiban atas kehilangan atau kerugian langsung, tidak langsung atau yang merupakan akibat yang ditanggung oleh pengguna dalam hubungannya dengan Layanan atau dalam hubungannya dengan penggunaan, ketidakmampuan menggunakan, atau akibat dari penggunaan Layanan, setiap situs web yang terkait dengannya dan setiap materi yang ditempatkan padanya.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Materi yang ditayangkan di Layanan adalah semata-mata untuk informasi guna membangun hubungan antara pemasang iklan dengan pengguna Layanan lainnya serta membantu pengguna Layanan untuk memutuskan mengajukan penawaran atau tidak mengajukan penawaran untuk barang atau jasa yang ditawarkan oleh Pemasang Iklan di Layanan.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Apabila sebagai pengguna Anda menggunakan informasi yang ada pada Layanan untuk kepentingan selain daripada yang dinyatakan pada paragraf di atas, maka kami dapat membatasi akses Anda terhadap Layanan serta mengambil langkah hukum yang diperlukan atas tindakan Anda.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("7. Pelaksanaan Aktivitas")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Pelaksanaan aktivitas akses ke Layanan tersedia untuk umum, walaupun begitu untuk memasang iklan Anda diwajibkan untuk menjadi pengguna terdaftar, untuk setiap pengguna terdaftar hanya dapat mendaftarkan 1 (satu) nomor telepon dan nomor telepon tersebut harus sama dengan nomor telepon pada profile Anda.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("SiMotor berhak menarik dan/atau mengubah Layanan yang disediakan tanpa pemberitahuan terlebih dahulu. Dari waktu ke waktu, SiMotor dapat membatasi akses ke beberapa bagian dari situs dan/atau keseluruhan dari Layanan kepada pengguna.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Apabila pengguna memilih atau diberi password (kode identifikasi pengguna) atau bagian informasi lainnya sebagai bagian dari prosedur keamanan Layanan, maka pengguna wajib menjaga kerahasiaan informasi tersebut dan tidak diperbolehkan mengungkapkannya kepada pihak ketiga.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("SiMotor berhak mematikan fungsi password (kode identifikasi pengguna), terlepas apakah dipilih oleh pengguna atau ditentukan oleh SiMotor dalam keadaan apapun dan dalam waktu kapanpun.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Sebagai pengguna terdaftar, Anda dapat kami berikan suatu tanda khusus termasuk namun tidak terbatas pada tujuan memvalidasi kebenaran informasi yang Anda berikan kepada kami. Validasi ini bersifat gratis, tidak wajib dan tidak dikenakan biaya apapun akan tetapi Anda diharapkan untuk berpartisipasi dalam kegiatan ini. Setelah kami memvalidasi kebenaran informasi yang Anda berikan, kami akan memberikan tanda khusus di akun Anda yang berlaku selama kami pandang patut, oleh karena itu kami harap Anda secara berkala dapat melakukan validasi ulang pada saat sistem kami memintanya. Berkenaan dengan tanda khusus tersebut kami juga dapat mengubah, menghapus, ataupun menambahkan tanda khusus tersebut. Tentunya apabila Anda melakukan pelanggaran pada sebagian atau seluruh ketentuan yang ada di SiMotor tanda tersebut dapat kami hilangkan dari akun Anda.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Pemberian tanda khusus terhadap pengguna maupun iklan yang dipasang oleh pengguna bukan merupakan bentuk dukungan, rekomendasi maupun pendapat dari SiMotor terhadap pengguna, barang/jasa yang ditawarkan, serta bagaimana pengguna mengekspresikan barang/jasa yang mereka tawarkan.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("9. Perubahan Layanan")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("SiMotor berhak memperbarui dan/atau mengubah tampilan dan/atau Layanan dan/atau isi Layanan secara rutin dan setiap saat, demi memberi pelayanan yang lebih baik kepada setiap penggunanya.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Apabila dibutuhkan, SiMotor dapat menangguhkan akses ke Layanan, atau menutupnya untuk sementara waktu untuk jangka waktu yang tidak tertentu.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v(" Berkenaan dengan informasi mengenai pengguna dan kunjungan pengguna ke Layanan, SiMotor berhak mengolah informasi mengenai pengguna sesuai dengan kebijakan yang tercantum di dalam "),e("a",{attrs:{href:""}},[a._v("Kebijakan Privasi.")]),a._v(" Dengan menggunakan Layanan, pengguna setuju atas pemrosesan tersebut dan menjamin semua data yang diberikan adalah benar. ")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("10. Mengunggah Materi ke Layanan")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Anda mengakui dan setuju bahwa Anda bertanggung jawab atas Materi yang Anda pasang, transmisikan melalui maupun tautkan kepada Layanan dan setiap konsekuensi yang timbul dari tindakan tersebut. Secara khusus Anda bertanggung jawab atas setiap Materi yang Anda unggah seperti email atau yang Anda sediakan melalui Layanan atau Situs. Sehubungan dengan Materi yang dipasang, ditransmisikan melalui maupun tautkan kepada Layanan oleh Anda, Anda menyatakan secara tegas bahwa:")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("10.1 Anda mempunyai dan akan tetap mempunyai hak atas Materi tersebut selama Materi tersebut masih tersedia di Layanan atau Situs; Anda mempunyai ijin, hak dan persetujuan untuk menggunakan Materi tersebut dan memberikan kewenangan kepada SiMotor untuk menggunakan Materi tersebut di dalam Layanan atau Situs atau untuk kepentingan promosi SiMotor atau untuk kepentingan lainnya sepanjang tidak bertentangan dengan Undang-Undang dan Syarat dan Ketentuan Umum ini; dan")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("10.2 Anda mempunyai persetujuan tertulis, pelepasan dan/atau ijin dari setiap dan seluruh individu yang dapat diidentifikasi maupun usaha yang terdapat di dalam Materi untuk menggunakan nama atau kemiripan dari setiap dan seluruh individu dan usaha tersebut di dalam Layanan atau Situs sesuai dengan Syarat dan Ketentuan Umum ini. Anda tetap memiliki hak milik atas setiap Materi; namun dengan memasukkan Materi ke dalam Layanan, Anda memberikan hak yang tidak dapat dibatalkan, tidak dapat dicabut kembali, berlaku terus menerus, di seluruh dunia, tidak eksklusif, bebas royalty dan dapat disublisensikan, dapat dipindahkan, untuk menggunakan, mereproduksi, mendistribusikan, membuat pekerjaan derivatif, memasang dan menampilkan Materi sehubungan dengan Layanan dan usaha SiMotor, termasuk namun tidak terbatas pada tujuan promosi dan redistribusi sebagian maupun seluruh dari Layanan dan Materi yang terdapat di dalamnya. Hak-hak ini diperlukan oleh SiMotor untuk dapat menyediakan Layanan dan memasang Materi Anda. Selanjutnya, dengan memasang Materi ke dalam area publik di dalam Layanan, Anda setuju untuk dan dengan ini memberikan kepada SiMotor seluruh hak yang diperlukan untuk melarang atau memperbolehkan pengumpulan, pemasangan, penyalinan, duplikasi, reproduksi atau eksploitasi Materi di dalam Layanan oleh pihak manapun untuk tujuan apapun. Anda juga dengan ini memberikan kepada setiap pengguna Layanan dan Situs sebuah hak non-eksklusif untuk mengakses Materi Anda melalui Layanan atau Situs. Hak ini akan tetap berlaku dan dengan segera berakhir pada saat Anda atau SiMotor menghapus atau menarik Materi dari Situs.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("SiMotor dapat memberlakukan batasan-batasan terhadap sebagian atau seluruh aktivitas Anda atau mengenakan biaya terhadap iklan yang dipasang oleh pengguna di dalam bagian-bagian tertentu dari Layanan termasuk namun tidak terbatas pada penggunaan Fitur Promosi Iklan yang harus dilakukan sesuai dengan Syarat dan Ketentuan Umum ini, serta Syarat dan Ketentuan Fitur Promosi Iklan.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Apabila pengguna memasang iklan di kategori iklan berbayar dan menggunakan fitur promosi iklan, pengguna memberikan hak kepada SiMotor untuk mengenakan biaya atas aktivitas tersebut.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("SiMotor berhak untuk menghapus setiap isi, materi atau bagian lain dari iklan, baik sebagian maupun keseluruhan, yang dipasang pengguna di Layanan atau Situs tanpa pemberitahuan terlebih dahulu kepada pengguna. Hal ini termasuk tapi tidak terbatas apabila iklan tersebut mengandung isi, materi atau bagian yang bersifat sensitif, ofensif, dapat memicu kebencian, mencemarkan nama baik, memuat materi yang memicu perpecahan suku, agama, ras, antar golongan, pornografi, perjudian, ataupun bertentangan dengan norma etika kesusilaan dan hukum yang berlaku di Republik Indonesia serta tidak sesuai dengan aturan yang berlaku di Layanan.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("11. Durasi Iklan")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Masa aktif iklan adalah sebagaimana tertera di halaman pemasangan iklan Setelah masa aktif iklan habis maka iklan secara otomatis dan permanen terhapus.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("12. Tautan (hyperlink) dari Layanan")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Layanan dapat mengandung tautan (hyperlink) ke situs dan/atau sumber daya lain yang disediakan oleh pihak ketiga, dimana SiMotor menegaskan bahwa tautan (hyperlink) tersebut disediakan hanya sebagai informasi bagi pengguna. SiMotor melepaskan diri dari kewajiban untuk mengontrol isi dari situs dan/atau sumber daya yang terjadi karena tautan (hyperlink) yang ada di Layanan.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("SiMotor tidak bertanggung jawab atas kehilangan dan/atau kerugian yang dapat timbul akibat penggunaan Layanan.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("13.Hubungan dengan Pihak Ketiga")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Anda mengakui dan setuju bahwa SiMotor tidak akan bertanggung jawab atas interaksi Anda dengan pihak ketiga di dalam Situs maupun melalui Layanan. Hal ini termasuk, namun tidak terbatas pada, pembayaran dan pengiriman barang dan jasa, serta syarat, kondisi, jaminan atau pernyataan lain yang terkait dengan interaksi antara Anda dengan pihak ketiga. Hubungan ini adalah antara Anda dengan pihak ketiga tersebut. Anda mengakui dan setuju bahwa SiMotor tidak akan bertanggung jawab atas setiap kerugian atau kerusakan yang timbul dari hubungan atau interaksi tersebut.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Dalam hal terjadi perselisihan antara pengguna dengan pihak ketiga, Anda mengerti dan setuju bahwa SiMotor tidak bertanggung jawab untuk melibatkan diri dalam perselisihan tersebut dan Anda dengan ini membebaskan SiMotor, karyawannya, petugasnya dan agennya dari setiap tuntutan dan kerugian yang timbul dalam bentuk apapun sehubungan dengan Layanan kami.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("14. Yurisdiksi dan Hukum yang Berlaku")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v(" Syarat & Ketentuan Umum ini, Kebijakan Privasi dan Larangan Iklan serta hubungan pengguna dengan SiMotor tunduk, diatur dan patuh menurut hukum Republik Indonesia, dan pengguna sepakat untuk terikat pada yurisdiksi hukum Republik Indonesia. Apabila terjadi perselisihan dalam penafsiran dan pelaksanaan Syarat & Ketentuan Umum ini, "),e("a",{attrs:{href:"/"}},[a._v("Kebijakan Privasi")]),a._v("dan "),e("a",{attrs:{href:"/"}},[a._v("Larangan Iklan")]),a._v(", para pihak sepakat untuk menyelesaikannya secara musyawarah untuk mufakat ")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("15. Perubahan‐Perubahan")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("15.1 Setiap syarat dan ketentuan serta petunjuk lain yang berlaku untuk Layanan dapat diubah atau diperbarui oleh SiMotor dari waktu ke waktu atas kebijakannya sendiri.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("15.2 Pengguna diharapkan untuk memeriksa halaman ini dari waktu ke waktu untuk memperhatikan setiap perubahan, karena hal tersebut mengikat pengguna.")]),e("p",{staticClass:"ml-3 ml-lg-3"},[a._v("15.3 Sebagian dari ketentuan yang terdapat dalam Syarat & Ketentuan Umum ini dapat digantikan oleh ketentuan-ketentuan atau pemberitahuan lain yang dipublikasi di bagian lain Layanan.")]),e("p",{staticClass:"ml-2 ml-lg-2 font-weight-bold"},[a._v("16. Lain‐Lain")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Dengan menjadi pengguna Layanan, Anda dapat memilih apakah Anda berkeinginan untuk menerima informasi‐informasi terkait Layanan, penggunaanya, serta hal‐hal lain sehubungan dengan penggunaan akun Anda, adapun informasi‐informasi tersebut akan kami kirimkan atas dasar itikad baik melalui notifikasi baik berupa email ataupun notifikasi aplikasi ataupun bentuk‐bentuk lain. Jika Anda memilih untuk tidak menerima informasi, Anda dapat memberitahu kami dengan menghubungi layanan dukungan pelanggan kami.")]),e("p",{staticClass:"ml-2 ml-lg-2"},[a._v("Apabila pengguna memiliki hal lain yang ingin disampaikan atau komentar maupun keprihatinan mengenai materi yang tampak di Situs dan layanan, silakan menghubungi layanan dukungan pelanggan kami")]),e("p",[a._v(" Terima kasih atas kunjungan Anda ke Situs dan Layanan kami. "),e("br"),e("br")]),e("p",[a._v(" Salam sukses, "),e("br"),e("br")]),e("p",[a._v("SiMotor")]),e("p",{staticClass:"font-italic"},[a._v("*Syarat dan Ketentuan Umum ini terakhir diperbaharui 03 Desember 2019")])])])])],1)],1)],1)},t=[],u=e("2877"),s=e("6544"),l=e.n(s),g=e("40dc"),k=e("8336"),m=e("b0af"),r=e("a523"),d=e("132d"),p=e("adda"),b={},o=Object(u["a"])(b,i,t,!1,null,null,null);n["default"]=o.exports;l()(o,{VAppBar:g["a"],VBtn:k["a"],VCard:m["a"],VContainer:r["a"],VIcon:d["a"],VImg:p["a"]})}}]);
